
use std;
use std::cmp::{min, max};

pub fn exec() {

    let input = [
        "",
        "",
        "",
        "",
        ""
    ];

    let mut x = 1;
    let mut y = 1;

    let mut part1 = 0;

    for line in input.iter() {

        for c in line.chars() {
            let dir = match c {
                'R' => (1, 0),
                'L' => (-1, 0),
                'U' => (0, -1),
                'D' => (0, 1),
                _ => panic!("WTF")
            };
            x = max(0, min(2, x+dir.0));
            y = max(0, min(2, y+dir.1));
        }

        let value = x + 1 + (y * 3);

        part1 = part1 * 10 + value;
    }

    println!("Part1: {:?}", part1);
    
    fn limits(p: i32) -> (i32, i32) { 
        return match p {
            0 | 4 => (2, 2),
            1 | 3 => (1, 3),
            2 => (0, 4),
            _ => panic!("WTF")
        };
    }

    x = 0;
    y = 2;

    let mut part2 = String::from("");

    let pad = vec![
    "  1",
    " 234",
    "56789",
    " ABC",
    "  D"
    ];

    for line in input.iter() {

        for c in line.chars() {
            let dir = match c {
                'R' => (1, 0),
                'L' => (-1, 0),
                'U' => (0, -1),
                'D' => (0, 1),
                _ => panic!("WTF")
            };

            let xlimits = limits(y);
            let ylimits = limits(x);

            x = max(xlimits.0, min(xlimits.1, x+dir.0));
            y = max(ylimits.0, min(ylimits.1, y+dir.1));
        }

        let key = pad[y as usize].chars().nth(x as usize).unwrap();

        part2.push(key);
    }

    println!("Part2: {:?}", part2);
}
